<?phpuse soft\helpers\Url;use soft\helpers\ArrayHelper;/** * create url * @param null $url * @param bool $scheme * @return string * @see \yii\helpers\Url::to() */function to($url = null, $scheme = false){    return Url::to($url, $scheme);}/** * @param $array * @param $from * @param $to * @param null $group * @return array * @see \yii\helpers\ArrayHelper::map() */function map($array, $from, $to, $group = null){    return ArrayHelper::map($array, $from, $to, $group);}function weekDay($id){    switch ($id) {        case 1:            return t("Dushanba");        case 2:            return t("Seshanba");        case 3:            return t("Chorshanba");        case 4:            return t("Payshanba");        case 5:            return t("Juma");        case 6:            return t("Shanba");        case 7:            return t("Yakshanba");    }}?>